#import "V3Model.h"

@interface V3Model()

@property (nonatomic) NSInteger animationDirection;
@property (nonatomic) NSString *base64ImageData;

@end

@implementation V3Model

- (NSInteger)animationDirection
{
    return 2;
}

- (NSString *)base64ImageData
{
    return @"";
}
- (NSTimeInterval)activationImageDuration
{
    return kANIMATION_DURATION;
}

#pragma mark - Images

- (UIImage *)firstImage
{
    NSData *imageData = [[NSData alloc] initWithBase64EncodedString:self.base64ImageData options:NSDataBase64DecodingIgnoreUnknownCharacters];
    return  [UIImage imageWithData:imageData];
}

- (V3AnimationDirection)firstDirection
{
    return (V3AnimationDirection)self.animationDirection;
}

@end
